// Helper to map Animal ID (1-60) to Psychegram Group (F1-F4, A5-A8, M9-M12)
export const getPsychegramGroup = (id: number) => {
  if ([11, 17, 32, 38].includes(id)) return "F1"; // Fawn
  if ([14, 20, 23, 26, 29, 35].includes(id)) return "F2"; // Sheep
  if ([2, 8, 41, 47].includes(id)) return "F4"; // Tanuki
  if ([5, 44, 50, 53, 56, 59].includes(id)) return "F3"; // Black Panther
  if ([1, 7, 42, 48].includes(id)) return "A5"; // Cheetah
  if ([4, 10, 16, 33, 39, 45].includes(id)) return "A6"; // Koala
  if ([12, 18, 31, 37].includes(id)) return "A7"; // Elephant
  if ([3, 9, 15, 34, 40, 46].includes(id)) return "A8"; // Monkey
  if ([13, 19, 24, 25, 30, 36].includes(id)) return "M9"; // Wolf
  if ([51, 52, 57, 58].includes(id)) return "M10"; // Lion
  if ([6, 43, 49, 54, 55, 60].includes(id)) return "M11"; // Tiger
  if ([21, 22, 27, 28].includes(id)) return "M12"; // Pegasus
  return "F1"; // Fallback
};

